tinymce.PluginManager.add('jrsmiley', function (editor, url)
{
    function showDialog() {
        editor.windowManager.open({
            title: 'Smiley',
            url: core_system_url + '/' + jrSmiley_url + '/emotions/__ajax=1',
            width: 400,
            height: 400,
            buttons: [
                {
                    text: 'Close',
                    onclick: 'close'
                }
            ]
        });
    }
    editor.addButton('jrsmiley', {
        icon: 'emoticons',
        tooltip: "Smileys",
        onclick: showDialog
    });
});

